import React from 'react';
import './weatherComponent.css';

const Weather = props => {
  return (
    <div className="container text-light">
      <div className="Card">
        <h1 className="text-white py-3">{props.cityname}</h1>
        <h5 className="py-4">
          <i className={`wi ${props.weatherIcon} display-1`} />
        </h5>

        {/* Get Celsius */}
        {props.temp_celsius ? (<h1 className="py-2">{props.temp_celsius}&deg;</h1>) : null}

        {/* Show max and min temperature */}
        {maxMinTemp(props.temp_min, props.temp_max)}

        {/* Weather description */}
        <h4 className="py-3"> {props.description.charAt(0).toUpperCase() + props.description.slice(1)} </h4>
      </div>
    </div>
  );
};

export default Weather;

function maxMinTemp(min, max) {
  if(max && min) {
    return (
      <h3>
        <span className="px-4">{min}&deg;</span>
        <span className="px-4">{max}&deg;</span>
      </h3>
    );
  }
}
